// src/components/IniciarSesion.js
import React, { useState } from 'react';
import { useAuth } from './Autenticacion';
import './IniciarSesion.css';

const IniciarSesion = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [toggleForm, setToggleForm] = useState(false);
  const [rol, setRol] = useState('cliente');
  const [nombre, setNombre] = useState('');
  const [nickname, setNickname] = useState('');
  const [numCelular, setNumCelular] = useState('');
  const [direccionResidencia, setDireccionResidencia] = useState('');
  const { login } = useAuth();

  // Expresiones regulares para validaciones
  const nombreRegex = /^[A-Za-záéíóúÁÉÍÓÚüÜñÑ\s]{3,20}$/; // Solo letras y espacios
  const nicknameRegex = /^[A-Za-z0-9]{3,15}$/; // Letras y números
  const passwordRegex = /^(?=.*[A-Z].*[A-Z])(?=.*\d.*\d)(?=.*[A-Za-z\d@$!%*?&])[A-Za-z\d@$!%*?&]{7,30}$/; // Al menos 7 caracteres, 4 números, 2 mayúsculas
  const emailRegex = /^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/; // Email válido

  const handleSignInSubmit = (e) => {
    e.preventDefault();
    if (email === 'admin@gmail.com' && password === '123') {
      login('admin');
    } else if (email === 'cliente@gmail.com' && password === '123') {
      login('cliente');
    } else if (email === 'artista@gmail.com' && password === '123') {
      login('artista');
    } else {
      setError('Correo o contraseña incorrectos');
    }
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();

    if (!nombre || !nombreRegex.test(nombre)) {
      setError('Nombre inválido. Debe tener entre 3 y 20 caracteres y solo contener letras y espacios.');
      return;
    }
    if (!nickname || !nicknameRegex.test(nickname)) {
      setError('Nickname inválido. Debe tener entre 3 y 15 caracteres y solo contener letras y números.');
      return;
    }
    if (!password || !passwordRegex.test(password)) {
      setError('Contraseña inválida. Debe contener al menos 7 caracteres, 4 números, 2 mayúsculas y caracteres especiales.');
      return;
    }
    if (!email || !emailRegex.test(email)) {
      setError('Correo electrónico inválido. Debe tener entre 5 y 35 caracteres.');
      return;
    }
    if (!rol) {
      setError('Debe seleccionar un tipo de usuario.');
      return;
    }
    if (!numCelular || numCelular.length < 10) {
      setError('El número de celular es inválido.');
      return;
    }
    if (!direccionResidencia) {
      setError('La dirección de residencia no puede estar vacía.');
      return;
    }

    // Si todas las validaciones pasan, simula el registro
    login(rol); // Llama a login con el rol seleccionado
  };

  const handleToggle = () => {
    setToggleForm((prev) => !prev);
  };

  return (
    <div className={`container ${toggleForm ? 'toggle' : ''}`}>
      <div className="container-form">
        <form className="sign-in" onSubmit={handleSignInSubmit}>
          <h2>Iniciar Sesión</h2>
          <span><strong>Use su correo y contraseña</strong></span>
          <div className="container-input">
            <ion-icon name="mail-outline"></ion-icon>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="container-input">
            <ion-icon name="lock-closed-outline"></ion-icon>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          <button type="submit" className="button">Iniciar Sesión</button>
        </form>
      </div>

      <div className="container-form">
        <form className="sign-up" onSubmit={handleRegisterSubmit}>
          <h2>Registrarse</h2>
          <span><strong>Use su correo electrónico para registrarse</strong></span>
          <div className="container-input">
            <ion-icon name="person-outline"></ion-icon>
            <input
              type="text"
              placeholder="Nombre"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              required
            />
          </div>
          <div className="container-input">
            <ion-icon name="person-outline"></ion-icon>
            <input
              type="text"
              placeholder="Nickname"
              value={nickname}
              onChange={(e) => setNickname(e.target.value)}
              required
            />
          </div>
          <div className="container-input">
            <ion-icon name="mail-outline"></ion-icon>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="container-input">
            <ion-icon name="lock-closed-outline"></ion-icon>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="container-input">
            <ion-icon name="call-outline"></ion-icon>
            <input
              type="number"
              placeholder="Celular"
              value={numCelular}
              onChange={(e) => setNumCelular(e.target.value)}
              required
            />
          </div>
          <div className="container-input">
            <ion-icon name="location-outline"></ion-icon>
            <input
              type="text"
              placeholder="Dirección de Residencia"
              value={direccionResidencia}
              onChange={(e) => setDireccionResidencia(e.target.value)}
              required
            />
          </div>
          <label>
            Rol:
            <select value={rol} onChange={(e) => setRol(e.target.value)}>
              <option value="cliente">Cliente</option>
              <option value="artista">Artista</option>
            </select>
          </label>
          {error && <p className="error">{error}</p>}
          <button type="submit" className="button">Registrarse</button>
        </form>
      </div>

      <div className="container-welcome">
        <div className="welcome-sign-up welcome">
          <h3>Bienvenido</h3>
          <p>Si aún no tiene una cuenta, regístrese para acceder a todas las funciones del sitio</p>
          <button className="button" onClick={handleToggle}>
            Registrarse
          </button>
        </div>
        <div className="welcome-sign-in welcome">
          <h3>Bienvenido</h3>
          <p>Si ya tiene una cuenta, inicie sesión para acceder a todas las funciones del sitio</p>
          <button className="button" onClick={handleToggle}>
            Iniciar Sesión
          </button>
        </div>
      </div>
    </div>
  );
};

export default IniciarSesion;
