// src\components\Carrrito.js
import React from 'react';
import './Carrito.css';

const Carrito = ({ productos, total, onComprar, onVaciar }) => {
  return (
    <div className="carrito">
      <h2>Carrito de Compras</h2>
      
      {productos.length === 0 ? (
        <p>Tu carrito está vacío.</p>
      ) : (
        <div className="carrito-lista">
          {productos.map((producto, index) => (
            <div key={index} className="carrito-item">
              <img src={producto.imagen} alt={producto.nombre} className="carrito-item-imagen" />
              <div className="carrito-item-detalles">
                <h3>{producto.nombre}</h3>
                <p>Cantidad: {producto.cantidad}</p>
                <p>Precio: ${producto.precio}</p>
              </div>
            </div>
          ))}
          <div className="carrito-total">
            <h3>Total: ${total}</h3>
          </div>
          <div className="carrito-botones">
            <button onClick={onComprar} className="carrito-boton comprar">Comprar</button>
            <button onClick={onVaciar} className="carrito-boton vaciar">Vaciar Carrito</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Carrito;
