//src/components/Autenticacion.js
import React, { createContext, useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState(null); // Nuevo estado para el rol del usuario
  const navigate = useNavigate();

  const login = (role) => {
    setIsAuthenticated(true);
    setUserRole(role); // Establece el rol del usuario al iniciar sesión

    // Redirige al perfil según el rol
    if (role === 'admin') {
      navigate('/admin');
    } else if (role === 'cliente') {
      navigate('/perfil-cliente');
    } else if (role === 'artista') {
      navigate('/perfil-artista');
    }
  };

  const logout = () => {
    setIsAuthenticated(false);
    setUserRole(null);
    navigate('/');
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, userRole, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
