// src/components/Registrarse.js
import React, { useState } from 'react';
import './Registrarse.css'; // Asegúrate de que el archivo CSS sea correcto

const Registrarse = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [numCelular, setNumCelular] = useState(''); // Estado para el celular
  const [direccionResidencia, setDireccionResidencia] = useState(''); // Estado para la dirección
  const [rol, setRol] = useState('cliente'); // Estado para el rol
  const [error, setError] = useState('');
  const [mensaje, setMensaje] = useState('');

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    if (nombre && email && password && numCelular && direccionResidencia) {
      setMensaje('¡Registro exitoso!');
      setError('');
      setNombre('');
      setEmail('');
      setPassword('');
      setNumCelular('');
      setDireccionResidencia('');
      setRol('cliente');
    } else {
      setError('Por favor, completa todos los campos');
    }
  };

  return (
    <div className="registro-container">
      <h1>Registrarse</h1>
      <form onSubmit={handleRegisterSubmit}>
        <div>
          <label>Nombre</label>
          <input
            type="text"
            placeholder="Nombre"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Email</label>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Contraseña</label>
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Celular</label>
          <input
            type="number"
            placeholder="Celular"
            value={numCelular}
            onChange={(e) => setNumCelular(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Dirección de Residencia</label>
          <input
            type="text"
            placeholder="Dirección de Residencia"
            value={direccionResidencia}
            onChange={(e) => setDireccionResidencia(e.target.value)}
            required
          />
        </div>

        <div>
          <label>
            Rol:
            <select value={rol} onChange={(e) => setRol(e.target.value)}>
              <option value="cliente">Cliente</option>
              <option value="artista">Artista</option>
            </select>
          </label>
        </div>

        {error && <p className="error">{error}</p>}
        {mensaje && <p className="success">{mensaje}</p>}

        <button type="submit">Registrarse</button>
      </form>
    </div>
  );
};

export default Registrarse;
