// src\components\Autenticacion.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe ser usado con el AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(null); // Start with null to check if it's loading
  const [userRole, setUserRole] = useState(null);
  const navigate = useNavigate();

  // Cargar los valores desde localStorage cuando el componente se monte
  useEffect(() => {
    const storedAuth = localStorage.getItem('isAuthenticated');
    const storedRole = localStorage.getItem('userRole');

    if (storedAuth === 'true' && storedRole) {
      setIsAuthenticated(true);
      setUserRole(storedRole);
    } else {
      setIsAuthenticated(false); // Set false if nothing is stored
    }
  }, []); // Ejecutar solo una vez al cargar el componente

  // useEffect para redirigir al usuario después de la autenticación
  useEffect(() => {
    if (isAuthenticated && userRole) {
      // Solo redirigir si la autenticación es exitosa
      if (userRole === 'admin') {
        navigate('/Admin');
      } else if (userRole === 'cliente') {
        navigate('/PerfilUsuarioCliente');
      } else if (userRole === 'artista') {
        navigate('/PerfilUsuarioArtista');
      }
    }
  }, [isAuthenticated, userRole, navigate]);

  // Función para iniciar sesión
  const login = (role) => {
    if (!['admin', 'cliente', 'artista'].includes(role)) {
      console.error('Rol de usuario inválido');
      return;
    }

    // Guardamos la sesión en el localStorage
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('userRole', role);

    // Actualizamos el estado local
    setIsAuthenticated(true);
    setUserRole(role);
  };

  // Función para cerrar sesión
  const logout = () => {
    // Limpiar el localStorage
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('userRole');

    // Limpiar el estado local
    setIsAuthenticated(false);
    setUserRole(null);

    // Redirigir a la página principal
    navigate('/');
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, userRole, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
