// src/components/IniciarSesion.js
import React, { useState, useEffect } from 'react';
import { useAuth } from './Autenticacion'; // Asegúrate de tener configurado el contexto de autenticación
import './IniciarSesion.css';

const IniciarSesion = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login, isAuthenticated } = useAuth();

  // Redirige automáticamente si el usuario está autenticado
  useEffect(() => {
    if (isAuthenticated) {
      window.location.href = '/admin'; // Redirige al usuario a la página de admin
    }
  }, [isAuthenticated]);

  const handleSignInSubmit = (e) => {
    e.preventDefault();
    if (email === 'usuario@ejemplo.com' && password === '123') {
      alert('Inicio de sesión exitoso');
      login(); // Actualiza el estado de autenticación en el contexto
    } else {
      setError('Correo o contraseña incorrectos');
    }
  };

  return (
    <div className="container">
      <div className="container-form">
        <form className="sign-in" onSubmit={handleSignInSubmit}>
          <h2>Iniciar Sesión</h2>
          <span><strong>Use su correo y contraseña</strong></span>
          <div className="container-input">
            <ion-icon name="mail-outline"></ion-icon>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="container-input">
            <ion-icon name="lock-closed-outline"></ion-icon>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          <button type="submit" className="button">Iniciar Sesión</button>
        </form>
      </div>

      <div className="container-welcome">
        <div className="welcome-sign-up welcome">
          <h3>Bienvenido</h3>
          <p>Si aún no tiene una cuenta, regístrese para acceder a todas las funciones del sitio</p>
          <button className="button">
            Registrarse
          </button>
        </div>
        <div className="welcome-sign-in welcome">
          <h3>Bienvenido</h3>
          <p>Si ya tiene una cuenta, inicie sesión para acceder a todas las funciones del sitio</p>
          <button className="button">
            Iniciar Sesión
          </button>
        </div>
      </div>
    </div>
  );
};

export default IniciarSesion;
