import React, { useState } from 'react';
import CamisetaPrevisualizacion from './CamisetaPrevisualizacion'; // Asegúrate de importar el componente

const EstampaDetalles = () => {
  // Estados para manejar las opciones seleccionadas
  const [color, setColor] = useState('blanco'); // Color de la camiseta
  const [imagenEstampa, setImagenEstampa] = useState(null); // Imagen de la estampa
  const [ubicacion, setUbicacion] = useState('centrado'); // Ubicación de la estampa

  // Funciones para actualizar el estado según las selecciones
  const manejarColor = (nuevoColor) => setColor(nuevoColor);
  const manejarEstampa = (nuevaImagen) => setImagenEstampa(nuevaImagen);
  const manejarUbicacion = (nuevaUbicacion) => setUbicacion(nuevaUbicacion);

  return (
    <div>
      <h2>Detalles de la Estampa</h2>
      
      {/* Selector de color */}
      <label htmlFor="color">Color de la camiseta: </label>
      <select id="color" onChange={(e) => manejarColor(e.target.value)}>
        <option value="blanco">Blanco</option>
        <option value="negro">Negro</option>
        <option value="azul">Azul</option>
        <option value="rojo">Rojo</option>
      </select>

      {/* Cargar una imagen de estampa */}
      <div>
        <label htmlFor="estampa">Subir Estampa: </label>
        <input 
          type="file" 
          id="estampa" 
          onChange={(e) => manejarEstampa(URL.createObjectURL(e.target.files[0]))} 
        />
      </div>

      {/* Selector de ubicación de la estampa */}
      <label htmlFor="ubicacion">Ubicación de la estampa: </label>
      <select id="ubicacion" onChange={(e) => manejarUbicacion(e.target.value)}>
        <option value="centrado">Centrado</option>
        <option value="izquierda">Izquierda</option>
        <option value="derecha">Derecha</option>
      </select>

      {/* Previsualización de la camiseta */}
      {imagenEstampa && (
        <div>
          <h3>Previsualización de la camiseta:</h3>
          <CamisetaPrevisualizacion 
            imagenEstampa={imagenEstampa} 
            color={color} 
            ubicacion={ubicacion} 
          />
        </div>
      )}
    </div>
  );
};

export default EstampaDetalles;
