import React, { useState, useEffect, useRef } from 'react';

const EstampaDetalle = ({ estampa, onClose }) => {
  const [color, setColor] = useState('blanco');
  const [ubicacion, setUbicacion] = useState('central');
  const [cantidad, setCantidad] = useState(1);
  const [diseno, setDiseno] = useState('predeterminado');
  const canvasRef = useRef(null);

  useEffect(() => {
    // Recuperamos el canvas y su contexto
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Cargamos la camiseta con el color seleccionado
    const camisetaImg = new Image();
    camisetaImg.src = `/Imagenes/camiseta_${color}.png`; // Ruta de la camiseta con el color seleccionado

    camisetaImg.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height); // Limpiamos el canvas antes de dibujar

      // Dibujamos la camiseta en el canvas
      ctx.drawImage(camisetaImg, 0, 0, canvas.width, canvas.height);

      // Cargamos la estampa (imagen)
      const estampaImg = new Image();
      estampaImg.src = estampa.imagen; // Ruta de la estampa

      estampaImg.onload = () => {
        // Calculamos la posición de la estampa dependiendo de la ubicación
        let xPos = 0;
        let yPos = 0;

        if (ubicacion === 'central') {
          xPos = (canvas.width - estampaImg.width) / 2;
          yPos = (canvas.height - estampaImg.height) / 2;
        } else if (ubicacion === 'izquierda') {
          xPos = 20;
          yPos = (canvas.height - estampaImg.height) / 2;
        } else if (ubicacion === 'derecha') {
          xPos = canvas.width - estampaImg.width - 20;
          yPos = (canvas.height - estampaImg.height) / 2;
        }

        // Dibujamos la estampa sobre la camiseta
        ctx.drawImage(estampaImg, xPos, yPos);
      };
    };
  }, [color, ubicacion, estampa]);

  return (
    <div className="detalle-contenedor">
      <button className="cerrar" onClick={onClose}>← Volver</button>

      <div className="detalle-imagen">
        {/* Canvas que muestra la camiseta con la estampa */}
        <canvas
          ref={canvasRef}
          width={300}
          height={400}
          style={{ border: '1px solid #ddd', borderRadius: '10px' }}
        />
      </div>

      <div className="detalle-info">
        <h2>{estampa.nombre}</h2>
        <span className="detalle-precio">Precio: ${estampa.precio}</span>

        {/* Opciones de personalización */}
        <label>
          Color:
          <select value={color} onChange={(e) => setColor(e.target.value)}>
            <option value="blanco">Blanco</option>
            <option value="negro">Negro</option>
            <option value="rojo">Rojo</option>
            <option value="azul">Azul</option>
          </select>
        </label>

        <label>
          Ubicación de la estampa:
          <select value={ubicacion} onChange={(e) => setUbicacion(e.target.value)}>
            <option value="central">Central</option>
            <option value="izquierda">Izquierda</option>
            <option value="derecha">Derecha</option>
          </select>
        </label>

        <label>
          Cantidad:
          <input
            type="number"
            min="1"
            value={cantidad}
            onChange={(e) => setCantidad(parseInt(e.target.value))}
          />
        </label>

        <div className="detalle-botones">
          <button className="boton-comprar" onClick={() => alert('Comprado')}>Comprar</button>
          <button className="boton-carrito" onClick={() => alert('Añadido al carrito')}>Agregar al carrito</button>
        </div>
      </div>
    </div>
  );
};

export default EstampaDetalle;
