import React, { useState, useRef, useEffect } from 'react';
import './EstampaDetalle.css';

const EstampaDetalle = ({ estampa, onClose }) => {
  const [color, setColor] = useState('blanco');
  const [talla, setTalla] = useState('M');
  const [ubicacion, setUbicacion] = useState('central');
  const [cantidad, setCantidad] = useState(1);
  const [diseno, setDiseno] = useState('predeterminado');

  const canvasRef = useRef(null);

  // Cambiar la imagen de la camiseta y la estampa en el canvas
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    
    const camisetaImg = new Image();
    camisetaImg.src = `/images/camiseta_${color}.png`; // Ruta de la camiseta según el color
    
    camisetaImg.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height); // Limpiar canvas
      ctx.drawImage(camisetaImg, 0, 0, canvas.width, canvas.height);

      const estampaImg = new Image();
      estampaImg.src = estampa.imagen;

      estampaImg.onload = () => {
        let xPos = 0;
        let yPos = 0;

        // Definir la posición de la estampa
        if (ubicacion === 'central') {
          xPos = (canvas.width - estampaImg.width) / 2;
          yPos = (canvas.height - estampaImg.height) / 2;
        } else if (ubicacion === 'izquierda') {
          xPos = 20;
          yPos = (canvas.height - estampaImg.height) / 2;
        } else if (ubicacion === 'derecha') {
          xPos = canvas.width - estampaImg.width - 20;
          yPos = (canvas.height - estampaImg.height) / 2;
        }

        ctx.drawImage(estampaImg, xPos, yPos);
      };
    };
  }, [color, ubicacion, estampa]);

  const handleCompra = () => {
    alert(`Comprando camiseta:
      Color: ${color}
      Talla: ${talla}
      Ubicación: ${ubicacion}
      Cantidad: ${cantidad}
      Diseño: ${diseno}`);
  };

  const handleAñadirAlCarrito = () => {
    alert(`Añadiendo camiseta al carrito:
      Color: ${color}
      Talla: ${talla}
      Ubicación: ${ubicacion}
      Cantidad: ${cantidad}
      Diseño: ${diseno}`);
  };

  return (
    <div className="detalle-contenedor">
      <button className="cerrar" onClick={onClose}>← Volver</button>
      
      <div className="detalle-imagen">
        <canvas
          ref={canvasRef}
          width={300}
          height={400}
          style={{ border: '1px solid #ddd', borderRadius: '10px' }}
        ></canvas>
      </div>

      <div className="detalle-info">
        <h2>{estampa.nombre}</h2>
        <span className="detalle-precio">Precio: ${estampa.precio}</span>

        {/* Opciones de personalización */}
        <label>
          Color:
          <select value={color} onChange={(e) => setColor(e.target.value)}>
            <option value="blanco">Blanco</option>
            <option value="negro">Negro</option>
            <option value="rojo">Rojo</option>
            <option value="azul">Azul</option>
          </select>
        </label>

        <label>
          Ubicación de la estampa:
          <select value={ubicacion} onChange={(e) => setUbicacion(e.target.value)}>
            <option value="central">Central</option>
            <option value="izquierda">Izquierda</option>
            <option value="derecha">Derecha</option>
          </select>
        </label>

        <label>
          Talla:
          <select value={talla} onChange={(e) => setTalla(e.target.value)}>
            <option value="S">S</option>
            <option value="M">M</option>
            <option value="L">L</option>
            <option value="XL">XL</option>
          </select>
        </label>

        <label>
          Cantidad:
          <input
            type="number"
            min="1"
            value={cantidad}
            onChange={(e) => setCantidad(parseInt(e.target.value))}
          />
        </label>

        <label>
          Diseño:
          <select value={diseno} onChange={(e) => setDiseno(e.target.value)}>
            <option value="predeterminado">Predeterminado</option>
            <option value="otro">Otro diseño</option>
          </select>
        </label>

        {/* Botones */}
        <div className="detalle-botones">
          <button className="boton-comprar" onClick={handleCompra}>Comprar</button>
          <button className="boton-carrito" onClick={handleAñadirAlCarrito}>Agregar al carrito</button>
        </div>

        {/* Descripción */}
        <div className="detalle-descripcion">
          <h4>Descripción</h4>
          <p>{estampa.descripcion}</p>
        </div>
      </div>
    </div>
  );
};

export default EstampaDetalle;
