import React, { createContext, useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(true);
  const [userRole, setUserRole] = useState(null);
  const navigate = useNavigate();

  // useEffect para verificar el estado de la autenticación al cargar la app
  useEffect(() => {
    const storedAuth = localStorage.getItem('isAuthenticated') === 'true';
    const storedRole = localStorage.getItem('userRole');

    // Verificamos si la autenticación es válida
    if (storedAuth && ['admin', 'cliente', 'artista'].includes(storedRole)) {
      setIsAuthenticated(true);
      setUserRole(storedRole);
    } else {
      setIsAuthenticated(false);
      setUserRole(null);
    }
  }, []);  // Solo se ejecuta una vez al montar el componente

  // Función para redirigir según el rol del usuario
  const redirectUser = (role) => {
    if (role === 'admin') {
      navigate('/Admin');
    } else if (role === 'cliente') {
      navigate('/PerfilUsuarioCliente');
    } else if (role === 'artista') {
      navigate('/PerfilUsuarioArtista');
    }
  };

  // Función para iniciar sesión
  const login = (role) => {
    if (!['admin', 'cliente', 'artista'].includes(role)) {
      console.error('Rol de usuario inválido');
      return;
    }

    // Guardamos la sesión en el localStorage
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('userRole', role);

    setIsAuthenticated(true);
    setUserRole(role);

    // Redirigimos al usuario según su rol
    redirectUser(role);
  };

  // Función para cerrar sesión
  const logout = () => {
    setIsAuthenticated(false);
    setUserRole(null);
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('userRole');

    // Redirigimos al usuario a la página principal
    navigate('/');
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, userRole, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
