// src/components/IniciarSesion.js
import React, { useState } from 'react';
import { useAuth } from './Autenticacion';
import './IniciarSesion.css';

const IniciarSesion = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [toggleForm, setToggleForm] = useState(false); // Estado para alternar formularios
  const [rol, setRol] = useState('cliente'); // Estado para seleccionar rol en el registro
  const { login } = useAuth();

  const handleSignInSubmit = (e) => {
    e.preventDefault();
    if (email === 'admin@gmail.com' && password === '123') {
      login('admin'); // Llama a login para iniciar sesión y navegar en admin
    } else if (email === 'cliente@gmail.com' && password === '123') {
      login('cliente'); // Llama a login para iniciar sesión y navegar en cliente
    } else if (email === 'artista@gmail.com' && password === '123') {
      login('artista'); // Llama a login para iniciar sesión y navegar en artista
    } else {
      setError('Correo o contraseña incorrectos');
    }
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    // Simular un registro usando el rol seleccionado
    login(rol); // Envía el rol al método login para autenticarse
  };

  const handleToggle = () => {
    setToggleForm((prev) => !prev); // Alterna el estado entre true y false
  };

  return (
    <div className={`container ${toggleForm ? 'toggle' : ''}`}>
      <div className="container-form">
        <form className="sign-in" onSubmit={handleSignInSubmit}>
          <h2>Iniciar Sesión</h2>
          <span><strong>Use su correo y contraseña</strong></span>
          <div className="container-input">
            <ion-icon name="mail-outline"></ion-icon>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="container-input">
            <ion-icon name="lock-closed-outline"></ion-icon>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          <button type="submit" className="button">Iniciar Sesión</button>
        </form>
      </div>

      <div className="container-form">
        <form className="sign-up" onSubmit={handleRegisterSubmit}>
          <h2>Registrarse</h2>
          <span><strong>Use su correo electrónico para registrarse</strong></span>
          <div className="container-input">
            <ion-icon name="person-outline"></ion-icon>
            <input type="text" placeholder="Nombre" required />
          </div>
          <div className="container-input">
            <ion-icon name="mail-outline"></ion-icon>
            <input type="email" placeholder="Email" required />
          </div>
          <div className="container-input">
            <ion-icon name="lock-closed-outline"></ion-icon>
            <input type="password" placeholder="Password" required />
          </div>
          <label>
            Rol:
            <select value={rol} onChange={(e) => setRol(e.target.value)}>
              <option value="cliente">Cliente</option>
              <option value="artista">Artista</option>
            </select>
          </label>
          <button type="submit" className="button">Registrarse</button>
        </form>
      </div>

      <div className="container-welcome">
        <div className="welcome-sign-up welcome">
          <h3>Bienvenido</h3>
          <p>Si aún no tiene una cuenta, regístrese para acceder a todas las funciones del sitio</p>
          <button className="button" onClick={handleToggle}>
            Registrarse
          </button>
        </div>
        <div className="welcome-sign-in welcome">
          <h3>Bienvenido</h3>
          <p>Si ya tiene una cuenta, inicie sesión para acceder a todas las funciones del sitio</p>
          <button className="button" onClick={handleToggle}>
            Iniciar Sesión
          </button>
        </div>
      </div>
    </div>
  );
};

export default IniciarSesion;
