// src/components/Registrarse.js
import React, { useState } from 'react';
import './Registrarse.css'; // Asegúrate de tener este archivo de estilos

const Registrarse = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [mensaje, setMensaje] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Aquí podrías agregar validaciones más complejas o integración con el backend
    if (nombre && email && password) {
      setMensaje('¡Registro exitoso!');
      setError('');
      // Resetea los campos o realiza la acción posterior que desees
      setNombre('');
      setEmail('');
      setPassword('');
    } else {
      setError('Por favor, completa todos los campos');
    }
  };

  return (
    <div className="container">
      <div className="container-form">
        <form className="sign-up" onSubmit={handleSubmit}>
          <h2>Registrarse</h2>
          <span><strong>Bienvenido</strong></span>
          <p>Si aún no tiene una cuenta registrada, la puedes crear aquí mismo.</p>
          
          <div className="container-input">
            <ion-icon name="person-outline"></ion-icon>
            <input 
              type="text" 
              placeholder="Nombre"
              value={nombre} 
              onChange={(e) => setNombre(e.target.value)} 
              required 
            />
          </div>
          <div className="container-input">
            <ion-icon name="mail-outline"></ion-icon>
            <input 
              type="email" 
              placeholder="Correo electrónico" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />
          </div>
          <div className="container-input">
            <ion-icon name="lock-closed-outline"></ion-icon>
            <input 
              type="password" 
              placeholder="Contraseña" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
          </div>

          {error && <p className="error">{error}</p>}
          {mensaje && <p className="success">{mensaje}</p>}
          <button type="submit" className="button">Registrarse</button>
        </form>
      </div>
    </div>
  );
};

export default Registrarse;
