// Autenticación - Contexto
export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(null); // Cambié el estado inicial a `null`
  const [userRole, setUserRole] = useState(null);  // Cambié el estado inicial a `null`
  const navigate = useNavigate();

  useEffect(() => {
    const storedAuth = localStorage.getItem('isAuthenticated');
    const storedRole = localStorage.getItem('userRole');
    
    if (storedAuth === 'true' && storedRole && ['admin', 'cliente', 'artista'].includes(storedRole)) {
      setIsAuthenticated(true);
      setUserRole(storedRole);
    } else {
      setIsAuthenticated(false);
      setUserRole(null);
    }
  }, []);

  // Asegurarnos de que la redirección solo ocurre después de que el estado esté listo
  const redirectUser = (role) => {
    if (role === 'admin') {
      navigate('/Admin');
    } else if (role === 'cliente') {
      navigate('/PerfilUsuarioCliente');
    } else if (role === 'artista') {
      navigate('/PerfilUsuarioArtista');
    }
  };

  const login = (role) => {
    if (!['admin', 'cliente', 'artista'].includes(role)) {
      console.error('Rol de usuario inválido');
      return;
    }

    // Guardamos la sesión en el localStorage
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('userRole', role);

    setIsAuthenticated(true);
    setUserRole(role);

    // Redirigir una vez que el estado esté actualizado
    redirectUser(role);
  };

  const logout = () => {
    setIsAuthenticated(false);
    setUserRole(null);
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('userRole');
    navigate('/');
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, userRole, login, logout }}>
      {isAuthenticated !== null ? children : null}  {/* Solo renderizamos el contenido cuando `isAuthenticated` esté definido */}
    </AuthContext.Provider>
  );
};
