// src/components/Header.js
import React, { useState } from 'react';
import './Header.css'; // Asegúrate de que el CSS esté configurado
import { useAuth } from './Autenticacion';
import { useNavigate } from 'react-router-dom';

function Header() {
  const { isAuthenticated, userRole, logout } = useAuth();
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false); // Estado para abrir/cerrar el menú

  // Manejar la apertura/cierre del menú deslizable
  const handleMenuToggle = () => {
    setMenuOpen(prevState => !prevState);
  };

  // Manejo de cierre de sesión
  const handleLogout = () => {
    logout();
    setMenuOpen(false); // Cerrar el menú al hacer logout
    navigate('/');
  };

  return (
    <header className="Header">
      <div className="Header-logo">
        {/* Aquí se coloca el logo como una imagen */}
        <img src="Logo.png" alt="Logo" className="logo-img" />
      </div>
      
      <nav className="Header-nav">
        <ul>
          <li><a href="/">Inicio</a></li>
          <li><a href="/Catalogo">Catálogo</a></li>
          <li><a href="/Contacto">Contáctanos</a></li>
          
          {/* Si el usuario está autenticado, mostramos el avatar y el menú */}
          {isAuthenticated && (
            <li className="avatar-container" onClick={handleMenuToggle}>
              <img
                src="user-avatar.jpg" // Aquí puedes colocar la imagen del usuario
                alt="Avatar"
                className="user-avatar"
              />
              {menuOpen && (
                <div className="dropdown-menu">
                  <ul>
                    <li><a href="/PerfilUsuarioCliente">Perfil</a></li>
                    <li><a href="/Compras">Compras</a></li>
                    <li><a href="/Carrito">Su carrito</a></li>
                    <li><button onClick={handleLogout}>Log Out</button></li>
                  </ul>
                </div>
              )}
            </li>
          )}
          
          {/* Si no está autenticado, mostramos los enlaces para iniciar sesión */}
          {!isAuthenticated && (
            <>
              <li><a href="/Iniciar-sesion">Iniciar Sesión</a></li>
              <li><a href="/Registrarse">Registrarse</a></li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
}

export default Header;
