import React from 'react';
import './Compras.css'; // Agrega tu archivo CSS para los estilos

const Compras = ({ compras }) => {
  if (!compras || compras.length === 0) {
    return <p>No tienes compras realizadas.</p>;
  }

  return (
    <div className="compras-container">
      <h2>Mis Compras</h2>
      {compras.map((compra, index) => (
        <div className="compra" key={index}>
          <h3>Camiseta {index + 1}</h3>
          <div className="compra-info">
            <div><strong>Color:</strong> {compra.color}</div>
            <div><strong>Talla:</strong> {compra.talla}</div>
            <div><strong>Material:</strong> {compra.material}</div>
            <div><strong>Ubicación de la estampa:</strong> {compra.ubicacion}</div>
            <div><strong>Tamaño de la estampa:</strong> {compra.tamañoEstampa}</div>
            <div><strong>Cantidad:</strong> {compra.cantidad}</div>
            <div><strong>Diseño:</strong> {compra.diseño === 'otro' ? compra.descripcionPersonalizada : compra.diseño}</div>
            <div><strong>Precio Total:</strong> ${compra.precioTotal.toLocaleString()}</div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Compras;
