// src/components/Registrarse.js
import React, { useState } from 'react';
import './Registrarse.css'; // Asegúrate de que el archivo CSS sea correcto

const Registrarse = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [mensaje, setMensaje] = useState('');

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    if (nombre && email && password) {
      setMensaje('¡Registro exitoso!');
      setError('');
      // Aquí podrías agregar una llamada a una API para registrar al usuario
      setNombre('');
      setEmail('');
      setPassword('');
    } else {
      setError('Por favor, completa todos los campos');
    }
  };

  return (
    <div className="container">
      <div className="container-form">
        <form className="sign-up" onSubmit={handleRegisterSubmit}>
          <h2>Registrarse</h2>
          <span><strong>Use su correo electrónico para registrarse</strong></span>
          <div className="container-input">
            <ion-icon name="person-outline"></ion-icon>
            <input
              type="text"
              placeholder="Nombre"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              required
            />
          </div>
          <div className="container-input">
            <ion-icon name="mail-outline"></ion-icon>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="container-input">
            <ion-icon name="lock-closed-outline"></ion-icon>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          {mensaje && <p className="success">{mensaje}</p>}
          <button type="submit" className="button">Registrarse</button>
        </form>
      </div>
    </div>
  );
};

export default Registrarse;
