//Catalogo.js
import React, { useState } from 'react';
import EstampaDetalle from './EstampaDetalle'; // Asegúrate de importar el componente correctamente
import DragonImg from '../Imagenes/Dragon.jpg';
import TigreImg from '../Imagenes/Tigre.jpg';
import LoboImg from '../Imagenes/Lobo.jpg';

const Catalogo = () => {
  const [estampas] = useState([
    { id: 1, nombre: 'Estampa Dragón', precio: 50000, imagen: DragonImg, descripcion: 'Descripción de la estampa dragón.' },
    { id: 2, nombre: 'Estampa Tigre', precio: 60000, imagen: TigreImg, descripcion: 'Descripción de la estampa tigre.' },
    { id: 3, nombre: 'Estampa Lobo', precio: 48000, imagen: LoboImg, descripcion: 'Descripción de la estampa lobo.' },
  ]);
  
  const [estampaSeleccionada, setEstampaSeleccionada] = useState(null); // Estado para la estampa seleccionada

  // Función para seleccionar una estampa
  const handleSeleccionarEstampa = (estampa) => {
    setEstampaSeleccionada(estampa); // Establece la estampa seleccionada
  };

  // Función para cerrar el detalle de la estampa y regresar al catálogo
  const handleCerrarDetalle = () => {
    setEstampaSeleccionada(null); // Vuelve a mostrar el catálogo
  };

  return (
    <div>
      {/* Si hay una estampa seleccionada, mostrar solo el detalle de la estampa */}
      {estampaSeleccionada ? (
        <EstampaDetalle estampa={estampaSeleccionada} onClose={handleCerrarDetalle} />
      ) : (
        <div>
          <h1>Catalógo de Estampas</h1>
          <div className="catalogo">
            {estampas.map((estampa) => (
              <div key={estampa.id} onClick={() => handleSeleccionarEstampa(estampa)}>
                <h2>{estampa.nombre}</h2>
                <img src={estampa.imagen} alt={estampa.nombre} />
                <p>Precio: ${estampa.precio}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Catalogo;
