import React, { useState, useEffect } from 'react';
import './EstampaDetalle.css';

const EstampaDetalle = ({ estampa, onClose }) => {
  const [color, setColor] = useState('blanco');
  const [ubicacion, setUbicacion] = useState('central');
  const [diseno, setDiseno] = useState('predeterminado');
  const [previewImage, setPreviewImage] = useState('');

  // Definir las ubicaciones de la estampa
  const ubicaciones = {
    central: { top: '50%', left: '50%', transform: 'translate(-50%, -50%)' },
    izquierda: { top: '50%', left: '20%', transform: 'translate(-50%, -50%)' },
    derecha: { top: '50%', left: '80%', transform: 'translate(-50%, -50%)' },
  };

  // Función para actualizar la previsualización de la camiseta
  useEffect(() => {
    // Se carga la imagen de la estampa seleccionada
    const estampaSeleccionada = `/Imagenes/${estampa.imagen}`;
    setPreviewImage(estampaSeleccionada);
  }, [estampa]);

  const handleColorChange = (newColor) => {
    setColor(newColor);
  };

  const handleUbicacionChange = (newUbicacion) => {
    setUbicacion(newUbicacion);
  };

  const handleDisenoChange = (newDiseno) => {
    setDiseno(newDiseno);
  };

  const handleCompra = () => {
    alert(`Comprando camiseta:
      Color: ${color}
      Ubicación: ${ubicacion}
      Diseño: ${diseno}`);
  };

  const handleAñadirAlCarrito = () => {
    alert(`Añadiendo camiseta al carrito:
      Color: ${color}
      Ubicación: ${ubicacion}
      Diseño: ${diseno}`);
  };

  return (
    <div className="detalle-contenedor">
      <button className="boton-devolver" onClick={onClose}>
        <span className="flecha">&#8592;</span>
      </button>
      <div className="detalle-imagen">
        <div className="previsualizacion-camiseta">
          {/* Imagen de la camiseta */}
          <img
            src="/Imagenes/camiseta.png"
            alt="Camiseta"
            className={`camiseta ${color}`}
          />
          {/* Estampa sobre la camiseta */}
          <img
            src={previewImage}
            alt="Estampa"
            className="estampa"
            style={ubicaciones[ubicacion]}
          />
        </div>
      </div>

      <div className="detalle-info">
        <h2>{estampa.nombre}</h2>
        <span className="detalle-precio">Precio: ${estampa.precio}</span>

        {/* Opciones de personalización */}
        <label>
          Color:
          <select value={color} onChange={(e) => handleColorChange(e.target.value)}>
            <option value="blanco">Blanco</option>
            <option value="negro">Negro</option>
            <option value="rojo">Rojo</option>
            <option value="azul">Azul</option>
          </select>
        </label>

        <label>
          Ubicación de la estampa:
          <select value={ubicacion} onChange={(e) => handleUbicacionChange(e.target.value)}>
            <option value="central">Central</option>
            <option value="izquierda">Izquierda</option>
            <option value="derecha">Derecha</option>
          </select>
        </label>

        <label>
          Diseño:
          <select value={diseno} onChange={(e) => handleDisenoChange(e.target.value)}>
            <option value="predeterminado">Predeterminado</option>
            <option value="otro">Otro diseño</option>
          </select>
        </label>

        {/* Botones */}
        <div className="detalle-botones">
          <button className="boton-comprar" onClick={handleCompra}>Comprar</button>
          <button className="boton-carrito" onClick={handleAñadirAlCarrito}>Agregar al carrito</button>
        </div>

        {/* Descripción */}
        <div className="detalle-descripcion">
          <h4>Descripción</h4>
          <p>{estampa.descripcion}</p>
        </div>
      </div>
    </div>
  );
};

export default EstampaDetalle;
