// src/components/EstampaDetalle.js
import React from 'react';import './EstampaDetalle.css';

const EstampaDetalle = ({ estampa, onClose }) => {
  const [color, setColor] = useState('blanco');
  const [talla, setTalla] = useState('M');
  const [ubicacion, setUbicacion] = useState('central');
  const [material, setMaterial] = useState('algodon');
  const [cantidad, setCantidad] = useState(1);
  const [diseno, setDiseno] = useState('predeterminado');

  const handleCompra = () => {
    alert(`Comprando camiseta:
      Color: ${color}
      Talla: ${talla}
      Ubicación: ${ubicacion}
      Material: ${material}
      Cantidad: ${cantidad}
      Diseño: ${diseno}`);
  };
  const handleañadiralcarrito = () => {
    alert(`Añadiendo camiseta al carrito:
      Color: ${color}
      Talla: ${talla}
      Ubicación: ${ubicacion}
      Material: ${material}
      Cantidad: ${cantidad}
      Diseño: ${diseno}`);
  };

  return (
    <div className="estampa-detalle">
      <button className="cerrar" onClick={onClose}>Cerrar</button>
      <h2>{estampa.nombre}</h2>
      <img src={estampa.imagen} alt={estampa.nombre} className="estampa-imagen" />
      <p className="estampa-precio">Precio: ${estampa.precio}</p>
      <p className="estampa-descripcion">{estampa.descripcion}</p>

      {/* Selección de color */}
      <label>
        Color:
        <select value={color} onChange={(e) => setColor(e.target.value)}>
          <option value="blanco">Blanco</option>
          <option value="negro">Negro</option>
          <option value="rojo">Rojo</option>
          <option value="azul">Azul</option>
        </select>
      </label>

      {/* Selección de talla */}
      <label>
        Talla:
        <select value={talla} onChange={(e) => setTalla(e.target.value)}>
          <option value="S">S</option>
          <option value="M">M</option>
          <option value="L">L</option>
          <option value="XL">XL</option>
        </select>
      </label>

      {/* Selección de ubicación de la estampa */}
      <label>
        Ubicación:
        <select value={ubicacion} onChange={(e) => setUbicacion(e.target.value)}>
          <option value="central">Central</option>
          <option value="izquierda">Izquierda</option>
          <option value="derecha">Derecha</option>
        </select>
      </label>

      {/* Selección de material */}
      <label>
        Material:
        <select value={material} onChange={(e) => setMaterial(e.target.value)}>
          <option value="algodon">Algodón</option>
          <option value="poliester">Poliéster</option>
        </select>
      </label>

      {/* Selección de cantidad */}
      <label>
        Cantidad:
        <input
          type="number"
          min="1"
          value={cantidad}
          onChange={(e) => setCantidad(parseInt(e.target.value))}
        />
      </label>

      {/* Selección de diseño */}
      <label>
        Diseño:
        <select value={diseno} onChange={(e) => setDiseno(e.target.value)}>
          <option value="predeterminado">Predeterminado</option>
          <option value="otro">Otro diseño</option>
        </select>
      </label>
      <div className="detalle-botones">
          <button className="btn-comprar" onClick={handleCompra}>Comprar</button>
          <button className="btn-añadirAlCarrito" onClick={handleañadiralcarrito}>añadirAlCarrito</button>
        </div>
    </div>
  );
};
export default EstampaDetalle;

