// EstampaDetalle.js
import React, { useState } from 'react';

const EstampaDetalle = ({ estampa, onClose }) => {
  const [color, setColor] = useState('blanco');
  const [talla, setTalla] = useState('M');

  const handleCompra = () => {
    // Aquí puedes implementar la lógica para la compra
    alert(`Comprando camiseta de color ${color} y talla ${talla}`);
  };

  return (
    <div className="estampa-detalle">
      <button onClick={onClose}>Cerrar</button>
      <h2>{estampa.nombre}</h2>
      <img src={estampa.imagen} alt={estampa.nombre} />
      <p>Precio: ${estampa.precio}</p>
      <p>Descripción: {estampa.descripcion}</p>

      <label>
        Color:
        <select value={color} onChange={(e) => setColor(e.target.value)}>
          <option value="blanco">Blanco</option>
          <option value="negro">Negro</option>
          <option value="rojo">Rojo</option>
          <option value="azul">Azul</option>
          {/* Agrega más colores según tus necesidades */}
        </select>
      </label>

      <label>
        Talla:
        <select value={talla} onChange={(e) => setTalla(e.target.value)}>
          <option value="S">S</option>
          <option value="M">M</option>
          <option value="L">L</option>
          <option value="XL">XL</option>
          {/* Agrega más tallas según tus necesidades */}
        </select>
      </label>

      <button onClick={handleCompra}>Comprar</button>
    </div>
  );
};

export default EstampaDetalle; // Asegúrate de que esta línea esté al final del archivo
