// src\components\Autenticacion.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe ser usado con el AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedAuth = localStorage.getItem('isAuthenticated') === 'true';
    const storedRole = localStorage.getItem('userRole');
    
    if (storedAuth && storedRole) {
      setIsAuthenticated(true);
      setUserRole(storedRole);
    } else {
      setIsAuthenticated(false);
      setUserRole(null);
    }
  }, []); // Solo se ejecuta una vez cuando el componente se monta

  // Función para iniciar sesión
  const login = (role) => {
    if (!['admin', 'cliente', 'artista'].includes(role)) {
      console.error('Rol de usuario inválido');
      return;
    }

    // Guardar el estado en localStorage
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('userRole', role);

    // Actualizar el estado local
    setIsAuthenticated(true);
    setUserRole(role);
  };

  // Función para cerrar sesión
  const logout = () => {
    // Eliminar del localStorage
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('userRole');

    // Limpiar el estado
    setIsAuthenticated(false);
    setUserRole(null);

    // Redirigir a la página principal
    navigate('/');
  };

  // useEffect para redirigir al usuario a su página según el rol después del login
  useEffect(() => {
    if (isAuthenticated && userRole) {
      // Retrasar la redirección para permitir que el estado se propague
      setTimeout(() => {
        if (userRole === 'admin') {
          navigate('/Admin');
        } else if (userRole === 'cliente') {
          navigate('/PerfilUsuarioCliente');
        } else if (userRole === 'artista') {
          navigate('/PerfilUsuarioArtista');
        }
      }, 100); // Retraso de 100ms para asegurar la propagación del estado
    }
  }, [isAuthenticated, userRole, navigate]); // Dependemos de isAuthenticated y userRole para manejar el redireccionamiento

  return (
    <AuthContext.Provider value={{ isAuthenticated, userRole, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
